$('body').load(loading_screen.finish());$('#homePageTriangleCheck').prop('checked',!1);jQuery.fn.exists=function(){return this.length>0}
$('#mobileCheck').change(function(){const nav=$('nav');const body=$('body');const hamburger=$('.hamburger');if($(this).is(':checked')&&nav.hasClass('active')==!1){$(window).scrollTop(0);nav.addClass('active');body.addClass('navActive').bind('touchmove',function(e){e.preventDefault()})}
else{nav.removeClass('active');body.removeClass('navActive').unbind('touchmove')}});$('nav a.clickable').on('click',function(){const thisButton=this;const triangleCheck=$('#homePageTriangleCheck');if(this.id!='home'){if($('#homePageTriangleCheck').exists()){triangleCheck.prop('checked',!0)}
setTimeout(function(){$('header').removeClass('headerHome');changeTabs(thisButton)},250)}
else{changeTabs(thisButton);if($('#homePageTriangleCheck').exists()){triangleCheck.prop('checked',!1)}}
if($(window).width()<=838){$('body').removeClass('navActive');setTimeout(function(){$('#mobileCheck').prop('checked',!1)},150)}});function changeTabs(x){$('nav a.active').removeClass('active');$('section.active').removeClass('active');$('section#'+x.id).addClass('active');$(x).addClass('active');$('#navCheck').prop('checked',!1);$('nav').removeClass('active');$('body').unbind('touchmove')}
